// Prepare document for backend processing with embedded binary data
const items = $input.all();
const results = [];

for (const item of items) {
  const json = item.json;
  const binary = item.binary || {};

  console.log(`\nğŸ”„ Preparing document for backend:`);
  console.log(`ğŸ“„ Document ID: ${json.documentId}`);
  console.log(`ğŸ“ Filename: ${json.filename}`);
  console.log(`ğŸ”— Has binary: ${Object.keys(binary).length > 0}`);
  console.log(`ğŸ“ Has text content: ${!!json.content}`);
  console.log(`ğŸŒ Download URL: ${!!json.downloadUrl}`);

  // Prepare document data for DOC.X backend
  const documentData = {
    documentId: json.documentId,
    filename: json.filename,
    source: json.source,
    mimeType: json.mimeType,
    metadata: json.metadata || {},
    downloadUrl: json.downloadUrl || null,
    parentMessage: json.parentMessage || null,
    parentLibrary: json.parentLibrary || null,
    parentSystem: json.parentSystem || null,
    parentChat: json.parentChat || null,
    timestamp: new Date().toISOString(),
    binaryData: null,  // Will be populated if binary data exists
    binary: {}  // Include binary metadata for backend
  };

  // Handle binary data by embedding it as base64 in JSON
  if (Object.keys(binary).length > 0) {
    console.log(`ğŸ“ Processing binary attachments for JSON embedding`);

    const firstBinaryKey = Object.keys(binary)[0];
    const binaryInfo = binary[firstBinaryKey];

    if (binaryInfo && binaryInfo.data) {
      // Embed binary data as base64 in the JSON
      documentData.binaryData = binaryInfo.data;
      documentData.fileSize = binaryInfo.fileSize || (binaryInfo.data ? Buffer.byteLength(binaryInfo.data, 'base64') : 0);
      documentData.mimeType = binaryInfo.mimeType || documentData.mimeType;
      documentData.hasBinary = true;
      documentData.binaryKey = firstBinaryKey;

      // Store binary metadata
      documentData.binary[firstBinaryKey] = {
        mimeType: binaryInfo.mimeType,
        fileName: binaryInfo.fileName || json.filename,
        fileSize: documentData.fileSize
      };

      console.log(`âœ… Binary data embedded: ${json.filename} (${documentData.fileSize} bytes)`);
    }
  }

  // Handle content extraction based on available data
  if (json.content && json.content.trim()) {
    // Direct text content available (email content, extracted text)
    documentData.content = json.content;
    documentData.contentSource = 'direct';
    console.log(`âœ… Using direct text content (${json.content.length} chars)`);
  } else if (documentData.hasBinary) {
    // Binary attachment available - will be extracted by backend
    documentData.content = null; // Will be extracted by backend
    documentData.contentSource = 'binary';
    console.log(`ï¿½ Using binary attachment for processing`);
  } else if (json.downloadUrl) {
    // Download URL available - backend will fetch and process
    documentData.content = null; // Will be downloaded and extracted by backend
    documentData.contentSource = 'download';
    console.log(`ğŸŒ Using download URL for processing`);
  } else {
    // Fallback - use any available text
    const fallbackContent = json.text || json.html || json.snippet || '';
    documentData.content = fallbackContent;
    documentData.contentSource = 'fallback';
    console.log(`âš ï¸ Using fallback content (${fallbackContent.length} chars)`);
  }

  // Important: Only return JSON data since we embedded binary as base64
  results.push({
    json: documentData
    // Note: No separate binary object since it's embedded in JSON
  });

  console.log(`âœ… Document prepared with content source: ${documentData.contentSource}`);
  if (documentData.hasBinary) {
    console.log(`ğŸ“¦ Binary data embedded in JSON payload`);
  }
}

console.log(`\nğŸ¯ Backend Preparation Summary:`);
console.log(`ğŸ“„ Total documents prepared: ${results.length}`);
console.log(`ğŸ“ Documents with embedded binary: ${results.filter(r => r.json.hasBinary).length}`);
console.log(`ğŸ“ Documents with direct content: ${results.filter(r => r.json.contentSource === 'direct').length}`);

return results;

// Prepare documents for download or direct processing
const items = $input.all();
const results = [];

for (const item of items) {
  const json = item.json;
  const binary = item.binary || {};

  console.log(`ğŸ”„ Preparing document: ${json.filename}`);
  console.log(`ğŸ“¥ Download URL: ${json.downloadUrl}`);
  console.log(`ğŸ“ Has attachment: ${json.metadata?.hasAttachment}`);
  console.log(`ğŸ’¾ Has binary data: ${json.metadata?.hasBinaryData}`);
  console.log(`ğŸ”— Binary keys: ${Object.keys(binary)}`);

  // Check if we already have binary data (from email attachments)
  if (json.metadata?.hasBinaryData && Object.keys(binary).length > 0) {
    console.log(`ğŸ“ Document already has binary data - ready for backend`);
    // Binary attachment is already processed, just pass it through
    results.push({
      json: {
        ...json,
        processingType: 'binary',
        needsDownload: false
      },
      binary: binary
    });
  }
  // Check if we have a valid download URL
  else if (json.downloadUrl && json.downloadUrl !== 'null' && json.downloadUrl !== '' && json.downloadUrl !== 'undefined') {
    console.log(`ğŸ“ Document has download URL - will download file`);
    // Mark for actual download
    results.push({
      json: {
        ...json,
        processingType: 'download',
        needsDownload: true
      }
    });
  } else {
    console.log(`ğŸ“§ Document is content-only - creating virtual file`);

    // Create virtual binary data for email content
    const emailContent = json.content || '';
    const contentBuffer = Buffer.from(emailContent, 'utf8');

    const virtualBinary = {
      data: {
        data: contentBuffer.toString('base64'),
        mimeType: json.mimeType || 'text/html',
        fileName: json.filename,
        fileSize: contentBuffer.length
      }
    };

    console.log(`ğŸ“„ Created virtual file: ${json.filename} (${contentBuffer.length} bytes)`);

    // Mark as already processed with virtual binary
    results.push({
      json: {
        ...json,
        processingType: 'content',
        needsDownload: false
      },
      binary: virtualBinary
    });
  }
}

console.log(`ğŸ¯ Prepared ${results.length} documents`);
return results;

