// ---------- Helper Functions ----------
function safeString(value) {
  if (value === undefined || value === null) return '';
  try {
    return String(value).trim();
  } catch {
    return '';
  }
}

function extractFromField(from) {
  // Gmail 'from' field can be a string, object, or nested array
  if (!from) return 'Unknown Sender';
  if (typeof from === 'string') return from;

  if (from.text) return from.text;
  if (from.value && Array.isArray(from.value) && from.value.length > 0) {
    const val = from.value[0];
    if (val.name && val.address) return `${val.name} <${val.address}>`;
    if (val.address) return val.address;
  }

  // Fallback for weird Gmail node structures
  if (from.address) return from.address;
  if (from.name) return from.name;

  return 'Unknown Sender';
}

function hasMultipartContent(headers) {
  if (!headers) return false;
  const contentType = headers['content-type'] || headers['Content-Type'];
  return typeof contentType === 'string' && contentType.includes('multipart/');
}

function extractBoundary(headers) {
  if (!headers) return null;
  const contentType = headers['content-type'] || headers['Content-Type'];
  const match =
    typeof contentType === 'string'
      ? contentType.match(/boundary="?([^";]+)"?/)
      : null;
  return match ? match[1] : null;
}

