const items = $input.all();
const results = [];

for (const item of items) {
  if (!item.json) continue;
  const data = item.json;
  const binary = item.binary || {};
  let hasProcessedAttachments = false;

  // ---------- Method 1: Binary Attachments ----------
  if (binary && Object.keys(binary).length > 0) {
    console.log(`ğŸ“ Found ${Object.keys(binary).length} binary attachments`);

    for (const [key, attachment] of Object.entries(binary)) {
      if (!attachment) continue;

      const fileName =
        attachment.fileName ||
        attachment.filename ||
        `attachment_${key}.${(attachment.mimeType || '').split('/')[1] || 'bin'}`;
      const fileSize = attachment.fileSize || 0;

      results.push({
        json: {
          documentId: `gmail_${data.id}_${fileName}`,
          filename: fileName,
          mimeType: attachment.mimeType || 'application/octet-stream',
          size: fileSize,
          source: 'gmail',
          parentMessage: {
            id: data.id,
            subject: safeString(data.subject),
            from: safeString(extractFromField(data.from)),
            date: data.date,
          },
          metadata: {
            hasAttachment: true,
            hasBinaryData: true,
            kmrlRelevance: 'confirmed',
            priority: 'normal',
          },
        },
        binary: { [key]: attachment },
      });
      hasProcessedAttachments = true;
    }
  }

  // ---------- Method 2: Gmail Processed Attachments ----------
  if (
    !hasProcessedAttachments &&
    data.attachments &&
    Array.isArray(data.attachments) &&
    data.attachments.length > 0
  ) {
    console.log(`ğŸ“ Found ${data.attachments.length} Gmail attachments`);
    for (const attachment of data.attachments) {
      const downloadUrl =
        attachment.downloadUrl ||
        attachment.url ||
        (attachment.attachmentId
          ? `https://www.googleapis.com/gmail/v1/users/me/messages/${data.id}/attachments/${attachment.attachmentId}`
          : null);

      results.push({
        json: {
          documentId: `gmail_${data.id}_${attachment.filename}`,
          filename: attachment.filename || 'attachment',
          mimeType: attachment.mimeType || 'application/octet-stream',
          size: attachment.size || 0,
          downloadUrl,
          source: 'gmail',
          parentMessage: {
            id: data.id,
            subject: safeString(data.subject),
            from: safeString(extractFromField(data.from)),
            date: data.date,
          },
          metadata: {
            hasAttachment: true,
            kmrlRelevance: 'confirmed',
            priority: 'normal',
          },
        },
      });
      hasProcessedAttachments = true;
    }
  }

  // ---------- Method 3: Multipart Emails ----------
  if (!hasProcessedAttachments && hasMultipartContent(data.headers)) {
    const boundary = extractBoundary(data.headers);
    results.push({
      json: {
        documentId: `gmail_${data.id}_multipart`,
        filename: `${safeString(data.subject) || 'KMRL Email'}.eml`,
        content: data.html || data.text || '',
        mimeType: 'message/rfc822',
        source: 'gmail',
        parentMessage: {
          id: data.id,
          subject: safeString(data.subject),
          from: safeString(extractFromField(data.from)),
          date: data.date,
        },
        metadata: {
          hasAttachment: true,
          multipartBoundary: boundary,
          kmrlRelevance: 'confirmed',
          priority: safeString(data.subject)
            .toLowerCase()
            .includes('urgent')
            ? 'urgent'
            : 'normal',
        },
      },
    });
    hasProcessedAttachments = true;
  }

  // ---------- Method 4: Plain Emails ----------
  if (!hasProcessedAttachments) {
    results.push({
      json: {
        documentId: `gmail_${data.id}_content`,
        filename: `${safeString(data.subject) || 'KMRL Email'}.txt`,
        content: data.text || data.html || '',
        mimeType: data.html ? 'text/html' : 'text/plain',
        source: 'gmail',
        parentMessage: {
          id: data.id,
          subject: safeString(data.subject),
          from: safeString(extractFromField(data.from)),
          date: data.date,
        },
        metadata: {
          hasAttachment: false,
          kmrlRelevance: 'confirmed',
          priority:
            safeString(data.subject).toLowerCase().includes('urgent') ||
            safeString(data.subject).toLowerCase().includes('à´…à´Ÿà´¿à´¯à´¨àµà´¤à´°')
              ? 'urgent'
              : 'normal',
        },
      },
    });
  }
}

